mapper.select:
SELECT articleInfo.articleId
FROM articleInfo
WHERE articleInfo.articleInfoDate > #{articleInfoDate}

SELECT count(*)
FROM support
WHERE articleId = #{articleId}
  AND flag = 1;

SELECT count(*)
FROM commentInfo
WHERE articleId = #{articleId}
  AND flag = 1;

SELECT count(*)
FROM browseInfo
WHERE articleId = #{articleId}
  AND flag = 1;

mapper.update:
UPDATE tag
SET tag.total = tag.total + 1
WHERE tag.tagName = #{text}
